dunes-script-module.script:
//
// Array of RestHosts
var arrRestHosts;
//
var restHost;
// Loop variable
var idx;


arrRestHosts = RESTHostManager.getHosts();

for (idx = 0; idx < arrRestHosts.length; idx ++){
	restHost = RESTHostManager.getHost(arrRestHosts[idx]);
	if (restHost.name.toLowerCase() == nsRestHostName.toLowerCase()) {
		return restHost;
	}
}

System.error("Couldn't find a RESTHost with name: " + nsRestHostName);
throw("Couldn't find a RESTHost with name: " + nsRestHostName);

//return null;
