dunes-script-module.script:
//
// Array of RestHosts
var arrRestOperations;
//
var restOperation;
// Loop variable
var idx;


arrRestOperations = nsRestHost.getOperations();

for (idx = 0; idx < arrRestOperations.length; idx ++){
	restOperation = nsRestHost.getOperation(arrRestOperations[idx]);
	if (restOperation.name.toLowerCase() == nsRestOperationName.toLowerCase()) {
		return restOperation;
	}
}

System.error("Could not find a RESTOperation with name: [" + nsRestOperationName + "] on host "+ nsRestHost.name );
throw("Could not find a RESTOperation with name: [" + nsRestOperationName + "] on host "+ nsRestHost.name );

//return null;
